import { Component } from 'react';
export default class InovuaScrollbar extends Component {
    constructor(props: any);
    shouldComponentUpdate(nextProps: any, nextState: any): any;
    setVisible(visible: any): void;
    setScrollPos(scrollPos: any): void;
    getScrollPos(): any;
    render(): JSX.Element;
    setOver(over: any): void;
    onMouseEnter(): void;
    onMouseLeave(): void;
    onTrackWheel(event: any): void;
    onTrackClick(event: any): void;
    getCoord(event: any): any;
    getThumbPosition(): number;
    onThumbMouseDown(event: any): void;
    onMouseMove(event: any): void;
    onScrollThumbScrollTop(scrollPos: any): void;
    onScrollThumbScrollLeft(scrollPos: any): void;
    onMouseUp(): void;
    getThumbTransform(orientation?: any, thumbSize?: number): string;
    thumbPosToScrollPos(thumbPos: any, orientation: any, thumbSize: any): number;
    scrollPosToThumbPos(scrollPos: any, orientation: any, thumbSize: any): number;
    getRatio(): number;
    getThumbSize(): number;
}
